import React, { useEffect, useState } from 'react';
import Navbar from './Components/Navbar';
import Footer from './Components/Footer';
import Main from './Components/Main';
import {
  createBrowserRouter,
  RouterProvider,
  Route,
  Link,
  Router,
  Routes,
} from "react-router-dom";


function App() {
  let tempTodo;

  if (localStorage.getItem("todos"))
    tempTodo = JSON.parse(localStorage.getItem("todos"));
  else
    tempTodo = [];

  const addTodo = (title, desc) => {
    let newSno;
    if (todos.length === 0)
      newSno = 1 
    else
      newSno = todos[0].sno + 1;

    const newTodo = {
      sno: newSno,
      title: title,
      desc: desc,
    }
    setTodos([newTodo, ...todos]);
  }

  const onDelete = (todo) => {
    setTodos(todos.filter((element) => {
      return (element !== todo);
    }));
  }

  const deleteAll = () => setTodos([]);

  const [todos, setTodos] = useState(tempTodo);
  useEffect(() => {
    localStorage.setItem("todos", JSON.stringify(todos))
  }, [todos])

  const router = createBrowserRouter([
    {
      path: "/",
      element: (
        <Main todos={todos} onDelete={onDelete} addTodo={addTodo} deleteAll={deleteAll}/>
      ),
    },
    {
      path: "about",
      element: <div>About</div>,
    },
  ]);

  return (
    <>
      <Navbar title="GirdharDo!" />
      <BrowserRouter>
        <Routes>
          <Route path="/" element={<Layout />}>
            <Route index element={<Home />} />
            <Route path="blogs" element={<Blogs />} />
            <Route path="contact" element={<Contact />} />
            <Route path="*" element={<NoPage />} />
          </Route>
        </Routes>
      </BrowserRouter>
      <Footer />
    </>
  );
}

export default App;
